current_mtu = initial_mtu;
maxsizesent = MINIMUM_MTU;
maxsizeacked = MINIMUM_MTU;
nsegrto = 0;
pending_message = 0;

if (pending_message)
     if (ack > claimedtcpseq){
          pending_message = 0;
          nsegrto = 0;
     }

else {
     if (claimedmtu > maxsizeacked){
          adjust_mtu();
          current_mtu = claimedmtu;
          maxsizesent = MINIMUM_MTU;
     }

          else {
               pending_message = 1;
               save_message();
          }
     }
}

if (pending_message && nsegrto >= MAXSEGRTO){
     adjust_mtu();
     nsegrto = 0;
     pending_message = 0;
     maxsizeacked = claimedmtu;
     maxsizesent = MINIMUM_MTU;
     current_mtu = claimedmtu;
}
