contents.text:
  };
  directory "/var/named";
  recursion no;        // authoritative-only server
  query-source address *;
};

logging {
  channel "querylog" {
    file "/var/log/query.log" versions 2 size 500m;
    print-time yes;
  };
  category queries { querylog; };
};

