enum {
    sip_registration_uri(1),
    sip_registration_route(2),
    (255)
} SipRegistrationType;

case sip_registration_route:
  opaque               contact_prefs<0..2^16-1>;
  Destination          destination_list<3..2^16-1>;

 struct {
    SipRegistrationType   type;
    uint16                length;
    SipRegistrationData   data;
} SipRegistration;
