      "IPv6 address of the next hop.";
  }
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:routes/rt:route/"
      + "rt:next-hop/rt:next-hop-options/rt:next-hop-list/"
      + "rt:next-hop-list/rt:next-hop" {
  when "derived-from-or-self(../../../../../rt:address-family, "
     + "'v6ur:ipv6-unicast')" {
    description
      "This augment is valid only for IPv6 unicast.";
  }
  description
    "This leaf augments the 'next-hop-list' case of IPv6 unicast
     routes.";
  leaf address {
    type inet:ipv6-address;
    description
      "IPv6 address of the next hop.";
  }
}

augment
  "/rt:routing-state/rt:ribs/rt:rib/rt:active-route/rt:input" {
  when "derived-from-or-self(../rt:address-family, "
     + "'v6ur:ipv6-unicast')" {
    description
      "This augment is valid only for IPv6 unicast RIBs.";
  }
  description
    "This augment adds the input parameter of the 'active-route'
     action.";
  leaf destination-address {
    type inet:ipv6-address; description
      "IPv6 destination address.";
  }
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:active-route/"
      + "rt:output/rt:route" {
  when "derived-from-or-self(../../rt:address-family, "
     + "'v6ur:ipv6-unicast')" {
    description
      "This augment is valid only for IPv6 unicast.";
  }
  description
    "This augment adds the destination prefix to the reply of the
     'active-route' action.";
  leaf destination-prefix {
    type inet:ipv6-prefix;
    description
      "IPv6 destination prefix.";
  }
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:active-route/"
      + "rt:output/rt:route/rt:next-hop/rt:next-hop-options/"
      + "rt:simple-next-hop" {
  when "derived-from-or-self(../../../rt:address-family, "
     + "'v6ur:ipv6-unicast')" {
    description
      "This augment is valid only for IPv6 unicast.";
  }
  description
    "Augment 'simple-next-hop' case in the reply to the
     'active-route' action.";
  leaf next-hop-address {
    type inet:ipv6-address;
    description
      "IPv6 address of the next hop.";
  }
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:active-route/"
      + "rt:output/rt:route/rt:next-hop/rt:next-hop-options/"
      + "rt:next-hop-list/rt:next-hop-list/rt:next-hop" {
  when "derived-from-or-self(../../../../../rt:address-family, "
     + "'v6ur:ipv6-unicast')" {
    description
      "This augment is valid only for IPv6 unicast.";
  } description
    "Augment 'next-hop-list' case in the reply to the
     'active-route' action.";
  leaf next-hop-address {
    type inet:ipv6-address;
    description
      "IPv6 address of the next hop.";
  }
}

augment "/rt:routing/rt:control-plane-protocols/"
      + "rt:control-plane-protocol/rt:static-routes" {
  description
    "This augment defines the configuration of the 'static'
     pseudo-protocol with data specific to IPv6 unicast.";
  container ipv6 {
    description
      "Configuration of a 'static' pseudo-protocol instance
       consists of a list of routes.";
    list route {
      key "destination-prefix";
      description
        "A list of static routes.";
      leaf destination-prefix {
        type inet:ipv6-prefix;
        mandatory "true";
        description
          "IPv6 destination prefix.";
      }
      leaf description {
        type string;
        description
          "Textual description of the route.";
      }
      container next-hop {
        description
          "Configuration of next-hop.";
        uses rt:next-hop-content {
          augment "next-hop-options/simple-next-hop" {
            description
              "Augment 'simple-next-hop' case in IPv6 static
               routes.";
            leaf next-hop-address {
              type inet:ipv6-address;
              description
                "IPv6 address of the next hop.";

              }
            }
            augment "next-hop-options/next-hop-list/next-hop-list/"
                  + "next-hop" {
              description
                "Augment 'next-hop-list' case in IPv6 static
                 routes.";
              leaf next-hop-address {
                type inet:ipv6-address;
                description
                  "IPv6 address of the next hop.";
              }
            }
          }
        }
      }
    }
  }
}

revision 2016-11-04 {
  description
    "Initial revision.";
  reference
    "RFC 8022: A YANG Data Model for Routing Management";
}

augment "/if:interfaces-state/if:interface/ip:ipv6" {
  description
    "Augment interface state data with parameters of IPv6 router
     advertisements.";
  container ipv6-router-advertisements {
    description
      "Parameters of IPv6 Router Advertisements.";
    leaf send-advertisements {
      type boolean;
      description
        "A flag indicating whether or not the router sends periodic
         Router Advertisements and responds to Router
         Solicitations.";
    }
    leaf max-rtr-adv-interval {
      type uint16 {
        range "4..1800";
      }
      units "seconds";
      description
        "The maximum time allowed between sending unsolicited
         multicast Router Advertisements from the interface.";
    }
    leaf min-rtr-adv-interval {
      type uint16 {
        range "3..1350";
      }
      units "seconds";
      description
        "The minimum time allowed between sending unsolicited
         multicast Router Advertisements from the interface.";
    }
    leaf managed-flag {
      type boolean;
      description
        "The value that is placed in the 'Managed address
         configuration' flag field in the Router Advertisement.";
    }
    leaf other-config-flag {
      type boolean;
      description
        "The value that is placed in the 'Other configuration' flag
         field in the Router Advertisement.";
    }
    leaf link-mtu {
      type uint32; description
        "The value that is placed in MTU options sent by the
         router.  A value of zero indicates that no MTU options are
         sent.";
    }
    leaf reachable-time {
      type uint32 {
        range "0..3600000";
      }
      units "milliseconds";
      description
        "The value that is placed in the Reachable Time field in
         the Router Advertisement messages sent by the router.  A
         value of zero means unspecified (by this router).";
    }
    leaf retrans-timer {
      type uint32;
      units "milliseconds";
      description
        "The value that is placed in the Retrans Timer field in the
         Router Advertisement messages sent by the router.  A value
         of zero means unspecified (by this router).";
    }

leaf cur-hop-limit {
  type uint8;
  description
    "The value that is placed in the Cur Hop Limit field in the
     Router Advertisement messages sent by the router.  A value
     of zero means unspecified (by this router).";
}
leaf default-lifetime {
  type uint16 {
    range "0..9000";
  }
  units "seconds";
  description
    "The value that is placed in the Router Lifetime field of
     Router Advertisements sent from the interface, in seconds.
     A value of zero indicates that the router is not to be
     used as a default router.";
}
container prefix-list {
  description
    "A list of prefixes that are placed in Prefix Information
     options in Router Advertisement messages sent from the
     interface.

   By default, these are all prefixes that the router
   advertises via routing protocols as being on-link for the
   interface from which the advertisement is sent.";
list prefix {
  key "prefix-spec";
  description
    "Advertised prefix entry and its parameters.";
  leaf prefix-spec {
    type inet:ipv6-prefix;
    description
      "IPv6 address prefix.";
  }
  leaf valid-lifetime {
    type uint32;
    units "seconds";
    description
      "The value that is placed in the Valid Lifetime in the
       Prefix Information option.  The designated value of
       all 1's (0xffffffff) represents infinity.

     An implementation SHOULD keep this value constant in
     consecutive advertisements except when it is
     explicitly changed in configuration.";
}
leaf on-link-flag {
  type boolean;
  description
    "The value that is placed in the on-link flag ('L-bit')
     field in the Prefix Information option.";
}
leaf preferred-lifetime {
  type uint32;
  units "seconds";
  description
    "The value that is placed in the Preferred Lifetime in
     the Prefix Information option, in seconds.  The
     designated value of all 1's (0xffffffff) represents
     infinity.

     An implementation SHOULD keep this value constant in
     consecutive advertisements except when it is
     explicitly changed in configuration.";
}
leaf autonomous-flag {
  type boolean;
  description
    "The value that is placed in the Autonomous Flag field
     in the Prefix Information option.";

augment "/if:interfaces/if:interface/ip:ipv6" {
  description
    "Augment interface configuration with parameters of IPv6 router
     advertisements.";
  container ipv6-router-advertisements {
    description
      "Configuration of IPv6 Router Advertisements.";
    leaf send-advertisements {
      type boolean;
      default "false";
      description
        "A flag indicating whether or not the router sends periodic
         Router Advertisements and responds to Router
         Solicitations.";
      reference
        "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
         AdvSendAdvertisements.";
    }
    leaf max-rtr-adv-interval {
      type uint16 {
        range "4..1800";
      }
      units "seconds";
      default "600";
      description
        "The maximum time allowed between sending unsolicited
         multicast Router Advertisements from the interface.";
      reference
        "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
         MaxRtrAdvInterval.";
    }
    leaf min-rtr-adv-interval {
      type uint16 {
        range "3..1350";
      }
      units "seconds";
      must ". <= 0.75 * ../max-rtr-adv-interval" {
        description
          "The value MUST NOT be greater than 75% of
           'max-rtr-adv-interval'.";

     - otherwise, it is 0.75 * max-rtr-adv-interval.";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     MinRtrAdvInterval.";
}
leaf managed-flag {
  type boolean;
  default "false";
  description
    "The value to be placed in the 'Managed address
     configuration' flag field in the Router Advertisement.";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvManagedFlag.";
}
leaf other-config-flag {
  type boolean;
  default "false";
  description
    "The value to be placed in the 'Other configuration' flag
     field in the Router Advertisement.";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvOtherConfigFlag.";
}
leaf link-mtu {
  type uint32;
  default "0";
  description
    "The value to be placed in MTU options sent by the router.
     A value of zero indicates that no MTU options are sent.";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvLinkMTU.";
}
leaf reachable-time {
  type uint32 {

    range "0..3600000";
  }
  units "milliseconds";
  default "0";
  description
    "The value to be placed in the Reachable Time field in the
     Router Advertisement messages sent by the router.  A value
     of zero means unspecified (by this router).";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvReachableTime.";
}
leaf retrans-timer {
  type uint32;
  units "milliseconds";
  default "0";
  description
    "The value to be placed in the Retrans Timer field in the
     Router Advertisement messages sent by the router.  A value
     of zero means unspecified (by this router).";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvRetransTimer.";
}
leaf cur-hop-limit {
  type uint8;
  description
    "The value to be placed in the Cur Hop Limit field in the
     Router Advertisement messages sent by the router.  A value
     of zero means unspecified (by this router).

     IANA: IP Parameters,
     http://www.iana.org/assignments/ip-parameters";
}
leaf default-lifetime {
  type uint16 {
    range "0..9000";
  }
  units "seconds";
  description
    "The value to be placed in the Router Lifetime field of

     If this parameter is not configured, the device SHOULD use
     a value of 3 * max-rtr-adv-interval.";
  reference
    "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
     AdvDefaultLifeTime.";
}
container prefix-list {
  description
    "Configuration of prefixes to be placed in Prefix
     Information options in Router Advertisement messages sent
     from the interface.

   The link-local prefix SHOULD NOT be included in the list
   of advertised prefixes.";
reference
  "RFC 4861: Neighbor Discovery for IP version 6 (IPv6) -
   AdvPrefixList.";
list prefix {
  key "prefix-spec";
  description
    "Configuration of an advertised prefix entry.";
  leaf prefix-spec {
    type inet:ipv6-prefix;
    description
      "IPv6 address prefix.";
  }
  choice control-adv-prefixes {
    default "advertise";
    description
      "Either the prefix is explicitly removed from the
       set of advertised prefixes, or the parameters with
       which it is advertised are specified (default case).";
    leaf no-advertise {
      type empty;
      description
        "The prefix will not be advertised.

                 This can be used for removing the prefix from the
                 default set of advertised prefixes.";
            }
            case advertise {
              leaf valid-lifetime {
                type uint32;
                units "seconds";
                default "2592000";
                description
                  "The value to be placed in the Valid Lifetime in
                   the Prefix Information option.  The designated
                   value of all 1's (0xffffffff) represents
                   infinity.";
                reference
                  "RFC 4861: Neighbor Discovery for IP version 6
                   (IPv6) - AdvValidLifetime.";
              }
              leaf on-link-flag {
                type boolean;
                default "true";
                description
                  "The value to be placed in the on-link flag
                   ('L-bit') field in the Prefix Information
                   option.";
                reference
                  "RFC 4861: Neighbor Discovery for IP version 6
                   (IPv6) - AdvOnLinkFlag.";
              }
              leaf preferred-lifetime {
                type uint32;
                units "seconds";
                must ". <= ../valid-lifetime" {
                  description
                    "This value MUST NOT be greater than
                     valid-lifetime.";
                }
                default "604800";
                description
                  "The value to be placed in the Preferred Lifetime
                   in the Prefix Information option.  The designated
                   value of all 1's (0xffffffff) represents
                   infinity.";
                reference
                  "RFC 4861: Neighbor Discovery for IP version 6
                   (IPv6) - AdvPreferredLifetime.";
              }
              leaf autonomous-flag {
                type boolean; default "true";
                description
                  "The value to be placed in the Autonomous Flag
                   field in the Prefix Information option.";
                reference
                  "RFC 4861: Neighbor Discovery for IP version 6
                   (IPv6) - AdvAutonomousFlag.";
              }
            }
          }
        }
      }
    }
  }
}

identity rip {
  base rt:routing-protocol;
  description
    "Identity for the Routing Information Protocol (RIP).";
}

grouping route-content {
  description
    "This grouping defines RIP-specific route attributes.";
  leaf metric {
    type rip-metric;
  }
  leaf tag {

    type uint16;
    default "0";
    description
      "This leaf may be used to carry additional info, e.g.,
       autonomous system (AS) number.";
  }
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:routes/rt:route" {
  when "derived-from-or-self(rt:source-protocol, 'rip:rip')" {
    description
      "This augment is only valid for a route whose source
       protocol is RIP.";
  }
  description
    "RIP-specific route attributes.";
  uses route-content;
}

augment "/rt:routing-state/rt:ribs/rt:rib/rt:active-route/"
      + "rt:output/rt:route" {
  description
    "RIP-specific route attributes in the output of 'active-route'
     RPC.";
  uses route-content;
}

augment "/rt:routing/rt:control-plane-protocols/"
      + "rt:control-plane-protocol" {
  when "derived-from-or-self(rt:type,'rip:rip')" {
    description
      "This augment is only valid for a routing protocol instance
       of type 'rip'.";
  }
  container rip {
    presence "RIP configuration";
    description
      "RIP instance configuration.";
    container interfaces {
      description
        "Per-interface RIP configuration.";
      list interface {
        key "name";
        description
          "RIP is enabled on interfaces that have an entry in this
           list, unless 'enabled' is set to 'false' for that
           entry.";
        leaf name {

            type if:interface-ref;
          }
          leaf enabled {
            type boolean;
            default "true";
          }
          leaf metric {
            type rip-metric;
            default "1";
          }
        }
      }
      leaf update-interval {
        type uint8 {
          range "10..60";
        }
        units "seconds";
        default "30";
        description
          "Time interval between periodic updates.";
      }
    }
  }
}

