module ietf-restconf {
  yang-version 1.1;
  namespace "urn:ietf:params:xml:ns:yang:ietf-restconf";
  prefix "rc";

revision 2017-01-26 {
  description
    "Initial revision.";
  reference
    "RFC 8040: RESTCONF Protocol.";
}

rc:yang-data yang-api {
  uses restconf;
} grouping errors {
  description
    "A grouping that contains a YANG container
     representing the syntax and semantics of a
     YANG Patch error report within a response message.";

list error {
  description
    "An entry containing information about one
     specific error that occurred while processing
     a RESTCONF request.";
  reference
    "RFC 6241, Section 4.3.";

leaf error-type {
  type enumeration {
    enum transport {
      description
        "The transport layer.";
    }
    enum rpc {
      description
        "The rpc or notification layer.";
    }
    enum protocol {
      description
        "The protocol operation layer.";
    }
    enum application {
      description
        "The server application layer.";
    }
  }
  mandatory true;
  description
    "The protocol layer where the error occurred.";
} leaf error-tag {
  type string;
  mandatory true;
  description
    "The enumerated error-tag.";
}

leaf error-app-tag {
  type string;
  description
    "The application-specific error-tag.";
}

leaf error-path {
  type instance-identifier;
  description
    "The YANG instance identifier associated
     with the error node.";
}

leaf error-message {
  type string;
  description
    "A message describing the error.";
}

           { 'ietf-system:system-restart' : [null] }
        ";
    } leaf yang-library-version {
      type string {
        pattern '\d{4}-\d{2}-\d{2}';
      }
      config false;
      mandatory true;
      description
        "Identifies the revision date of the 'ietf-yang-library'
         module that is implemented by this RESTCONF server.
         Indicates the year, month, and day in YYYY-MM-DD
         numeric format.";
    }
  }
}

module ietf-restconf-monitoring {
  namespace "urn:ietf:params:xml:ns:yang:ietf-restconf-monitoring";
  prefix "rcmon";

revision 2017-01-26 {
  description
    "Initial revision.";
  reference
    "RFC 8040: RESTCONF Protocol.";
}

container restconf-state {
  config false;
  description
    "Contains RESTCONF protocol monitoring information.";

  leaf-list capability {
    type inet:uri;
    description
      "A RESTCONF protocol capability URI.";
  }
} container streams {
  description
    "Container representing the notification event streams
     supported by the server.";
   reference
     "RFC 5277, Section 3.4, <streams> element.";

list stream {
  key name;
  description
    "Each entry describes an event stream supported by
     the server.";

leaf name {
  type string;
  description
    "The stream name.";
  reference
    "RFC 5277, Section 3.4, <name> element.";
}

leaf description {
  type string;
  description
    "Description of stream content.";
  reference
    "RFC 5277, Section 3.4, <description> element.";
}

leaf replay-support {
  type boolean;
  default false;
  description
    "Indicates if replay buffer is supported for this stream.
     If 'true', then the server MUST support the 'start-time'
     and 'stop-time' query parameters for this stream.";
  reference
    "RFC 5277, Section 3.4, <replaySupport> element.";
} leaf replay-log-creation-time {
  when "../replay-support" {
    description
      "Only present if notification replay is supported.";
  }
  type yang:date-and-time;
  description
    "Indicates the time the replay log for this stream
     was created.";
  reference
    "RFC 5277, Section 3.4, <replayLogCreationTime>
     element.";
}

list access {
  key encoding;
  min-elements 1;
  description
    "The server will create an entry in this list for each
     encoding format that is supported for this stream.
     The media type 'text/event-stream' is expected
     for all event streams.  This list identifies the
     subtypes supported for this stream.";

        leaf encoding {
          type string;
          description
            "This is the secondary encoding format within the
             'text/event-stream' encoding used by all streams.
             The type 'xml' is supported for XML encoding.
             The type 'json' is supported for JSON encoding.";
        } leaf location {
          type inet:uri;
          mandatory true;
          description
            "Contains a URL that represents the entry point
             for establishing notification delivery via
             server-sent events.";
        }
      }
    }
  }
}

organization "Example, Inc.";
contact "support at example.com";
description "Example Jukebox Data Model Module.";
revision "2016-08-15" {
  description "Initial version.";
  reference "example.com document 1-4673.";
}

// abbreviated list of genre classifications
identity alternative {
  base genre;
  description
    "Alternative music.";
}
identity blues {
  base genre;
  description
    "Blues music.";
}
identity country {
  base genre;
  description
    "Country music.";
}
identity jazz {
  base genre;
  description
    "Jazz music.";
}
identity pop {
  base genre;
  description
    "Pop music.";
} identity rock {
  base genre;
  description
    "Rock music.";
}

list artist {
  key name;
  description
    "Represents one 'artist' resource within the
     'jukebox' library resource.";

leaf name {
  type string {
    length "1 .. max";
  }
  description
    "The name of the artist.";
}

list album {
  key name;
  description
    "Represents one 'album' resource within one
     'artist' resource, within the jukebox library.";

leaf name {
  type string {
    length "1 .. max";
  }
  description
    "The name of the album.";
} leaf genre {
  type identityref { base genre; }
  description
    "The genre identifying the type of music on
     the album.";
}

leaf year {
  type uint16 {
    range "1900 .. max";
  }
  description
    "The year the album was released.";
}

  leaf label {
    type string;
    description
      "The label that released the album.";
  }
  leaf catalogue-number {
    type string;
    description
      "The album's catalogue number.";
  }
}

list song {
  key name;
  description
    "Represents one 'song' resource within one
     'album' resource, within the jukebox library.";

      leaf name {
        type string {
           length "1 .. max";
        }
        description
          "The name of the song.";
      } leaf location {
        type string;
        mandatory true;
        description
          "The file location string of the
           media file for the song.";
      }
      leaf format {
        type string;
        description
          "An identifier string for the media type
           for the file associated with the
           'location' leaf for this entry.";
      }
      leaf length {
        type uint32;
        units "seconds";
        description
          "The duration of this song in seconds.";
      }
    }   // end list 'song'
  }   // end list 'album'
}  // end list 'artist'

  leaf artist-count {
     type uint32;
     units "artists";
     config false;
     description
       "Number of artists in the library.";
  }
  leaf album-count {
     type uint32;
     units "albums";
     config false;
     description
       "Number of albums in the library.";
  }
  leaf song-count {
     type uint32;
     units "songs";
     config false;
     description
       "Number of songs in the library.";
  }
}  // end library list playlist {
  key name;
  description
    "Example configuration data resource.";

leaf name {
  type string;
  description
    "The name of the playlist.";
}
leaf description {
  type string;
  description
    "A comment describing the playlist.";
}
list song {
  key index;
  ordered-by user;

    leaf index {    // not really needed
      type uint32;
      description
        "An arbitrary integer index for this playlist song.";
    }
    leaf id {
      type instance-identifier;
      mandatory true;
      description
        "Song identifier.  Must identify an instance of
         /jukebox/library/artist/album/song/name.";
    }
  }
} container player {
  description
    "Represents the jukebox player resource.";

    leaf gap {
      type decimal64 {
        fraction-digits 1;
        range "0.0 .. 2.0";
      }
      units "tenths of seconds";
      description
        "Time gap between each song.";
    }
  }
}

   rpc play {
     description
       "Control function for the jukebox player.";
     input {
       leaf playlist {
         type string;
         mandatory true;
         description
           "The playlist name.";
       }
       leaf song-number {
         type uint32;
         mandatory true;
         description
           "Song number in playlist to play.";
       }
     }
   }
}

container events {
  list event {
    key name;
    leaf name { type string; }
    leaf description { type string; }
    leaf event-count {
      type uint32;
      config false;
    }
  }
}

