contents.text:
revision 2017-01-27 {
 description
  "Initial document.";
 reference
   "RFC 8049.";
}

feature cloud-access {
 description
  "Allows the VPN to connect to a CSP.";
}
feature multicast {
 description
  "Enables multicast capabilities in a VPN.";
}
feature ipv4 {
 description
  "Enables IPv4 support in a VPN.";
}
feature ipv6 {
 description
  "Enables IPv6 support in a VPN.";
}
feature carrierscarrier {
 description
  "Enables support of CsC.";
}
feature extranet-vpn {
 description
  "Enables support of extranet VPNs.";
}
feature site-diversity {
 description
  "Enables support of site diversity constraints.";
}
feature encryption {
 description
  "Enables support of encryption.";
}
feature qos {
 description
  "Enables support of classes of services.";
}
feature qos-custom {
 description
  "Enables support of the custom QoS profile.";
}
feature rtg-bgp {
 description
  "Enables support of the BGP routing protocol.";
} feature rtg-rip {
 description
  "Enables support of the RIP routing protocol.";
}
feature rtg-ospf {
 description
  "Enables support of the OSPF routing protocol.";
}
feature rtg-ospf-sham-link {
 description
  "Enables support of OSPF sham links.";
}
feature rtg-vrrp {
 description
  "Enables support of the VRRP routing protocol.";
}
feature fast-reroute {
 description
  "Enables support of Fast Reroute.";
}
feature bfd {
 description
  "Enables support of BFD.";
}
feature always-on {
 description
  "Enables support of the 'always-on' access constraint.";
}
feature requested-type {
 description
  "Enables support of the 'requested-type' access constraint.";
}
feature bearer-reference {
 description
  "Enables support of the 'bearer-reference' access constraint.";
}

typedef svc-id {
 type string;
 description
  "Defines a type of service component identifier.";
} typedef template-id {
 type string;
 description
  "Defines a type of service template identifier.";
}

typedef address-family {
 type enumeration {
  enum ipv4 {
   description
    "IPv4 address family.";
  }
  enum ipv6 {
   description
    "IPv6 address family.";
  }
 }
 description
  "Defines a type for the address family.";
}

identity site-network-access-type {
 description
  "Base identity for site-network-access type.";
}
identity point-to-point {
 base site-network-access-type;
 description
  "Identity for point-to-point connection.";
}
identity multipoint {
 base site-network-access-type;
 description
  "Identity for multipoint connection.
  Example: Ethernet broadcast segment.";
}
identity placement-diversity {
 description
  "Base identity for site placement constraints.";
}
identity bearer-diverse {
 base placement-diversity;
 description
  "Identity for bearer diversity.
  The bearers should not use common elements.";
} identity pe-diverse {
 base placement-diversity;
 description
  "Identity for PE diversity.";
}
identity pop-diverse {
 base placement-diversity;
 description
  "Identity for POP diversity.";
}
identity linecard-diverse {
 base placement-diversity;
 description
  "Identity for linecard diversity.";
}
identity same-pe {
 base placement-diversity;
 description
  "Identity for having sites connected on the same PE.";
}
identity same-bearer {
 base placement-diversity;
 description
  "Identity for having sites connected using the same bearer.";
}
identity customer-application {
 description
  "Base identity for customer application.";
}
identity web {
 base customer-application;
 description
  "Identity for Web application (e.g., HTTP, HTTPS).";
}
identity mail {
 base customer-application;
 description
  "Identity for mail application.";
}
identity file-transfer {
 base customer-application;
 description
  "Identity for file transfer application (e.g., FTP, SFTP).";
} identity database {
 base customer-application;
 description
  "Identity for database application.";
}
identity social {
 base customer-application;
 description
  "Identity for social-network application.";
}
identity games {
 base customer-application;
 description
  "Identity for gaming application.";
}
identity p2p {
 base customer-application;
 description
  "Identity for peer-to-peer application.";
}
identity network-management {
 base customer-application;
 description
  "Identity for management application
  (e.g., Telnet, syslog, SNMP).";
}
identity voice {
 base customer-application;
 description
  "Identity for voice application.";
}
identity video {
 base customer-application;
 description
  "Identity for video conference application.";
}
identity site-vpn-flavor {
 description
  "Base identity for the site VPN service flavor.";
}
identity site-vpn-flavor-single {
 base site-vpn-flavor;
 description
  "Base identity for the site VPN service flavor.
  Used when the site belongs to only one VPN.";
} identity site-vpn-flavor-multi {
 base site-vpn-flavor;
 description
  "Base identity for the site VPN service flavor.
  Used when a logical connection of a site
  belongs to multiple VPNs.";
}
identity site-vpn-flavor-sub {
 base site-vpn-flavor;
 description
  "Base identity for the site VPN service flavor.
  Used when a site has multiple logical connections.
  Each connection may belong to different multiple VPNs.";
}
identity site-vpn-flavor-nni {
 base site-vpn-flavor;
 description
  "Base identity for the site VPN service flavor.
  Used to describe an NNI option A connection.";
}
identity management {
 description
  "Base identity for site management scheme.";
}
identity co-managed {
 base management;
 description
  "Base identity for co-managed site.";
}
identity customer-managed {
 base management;
 description
  "Base identity for customer-managed site.";
}
identity provider-managed {
 base management;
 description
  "Base identity for provider-managed site.";
}
identity address-allocation-type {
 description
  "Base identity for address-allocation-type for PE-CE link.";
}
identity provider-dhcp {
 base address-allocation-type;
 description
  "Provider network provides DHCP service to customer.";
} identity provider-dhcp-relay {
 base address-allocation-type;
 description
  "Provider network provides DHCP relay service to customer.";
}
identity provider-dhcp-slaac {
 base address-allocation-type;
 description
  "Provider network provides DHCP service to customer,
  as well as SLAAC.";
}
identity static-address {
 base address-allocation-type;
 description
  "Provider-to-customer addressing is static.";
}
identity slaac {
 base address-allocation-type;
 description
  "Use IPv6 SLAAC.";
}

identity site-role {
 description
  "Base identity for site type.";
}
identity any-to-any-role {
 base site-role;
 description
  "Site in an any-to-any IP VPN.";
}
identity spoke-role {
 base site-role;
 description
  "Spoke site in a Hub-and-Spoke IP VPN.";
}
identity hub-role {
 base site-role;
 description
  "Hub site in a Hub-and-Spoke IP VPN.";
} identity vpn-topology {
 description
  "Base identity for VPN topology.";
}
identity any-to-any {
 base vpn-topology;
 description
  "Identity for any-to-any VPN topology.";
}
identity hub-spoke {
 base vpn-topology;
 description
  "Identity for Hub-and-Spoke VPN topology.";
}
identity hub-spoke-disjoint {
 base vpn-topology;
 description
  "Identity for Hub-and-Spoke VPN topology
  where Hubs cannot communicate with each other.";
}

identity multicast-tree-type {
 description
  "Base identity for multicast tree type.";
}
identity ssm-tree-type {
 base multicast-tree-type;
 description
  "Identity for SSM tree type.";
}
identity asm-tree-type {
 base multicast-tree-type;
 description
  "Identity for ASM tree type.";
}
identity bidir-tree-type {
 base multicast-tree-type;
 description
  "Identity for bidirectional tree type.";
}

identity multicast-rp-discovery-type {
 description
  "Base identity for RP discovery type.";
} identity auto-rp {
 base multicast-rp-discovery-type;
 description
  "Base identity for Auto-RP discovery type.";
}
identity static-rp {
 base multicast-rp-discovery-type;
 description
  "Base identity for static type.";
}
identity bsr-rp {
 base multicast-rp-discovery-type;
 description
  "Base identity for BSR discovery type.";
}

identity routing-protocol-type {
 description
  "Base identity for routing protocol type.";
}
identity ospf {
 base routing-protocol-type;
 description
  "Identity for OSPF protocol type.";
}
identity bgp {
 base routing-protocol-type;
 description
  "Identity for BGP protocol type.";
}
identity static {
 base routing-protocol-type;
 description
  "Identity for static routing protocol type.";
}
identity rip {
 base routing-protocol-type;
 description
  "Identity for RIP protocol type.";
}
identity vrrp {
 base routing-protocol-type;
 description
  "Identity for VRRP protocol type.
  This is to be used when LANs are directly connected
  to PE routers.";
} identity direct {
 base routing-protocol-type;
 description
  "Identity for direct protocol type.";
}

identity protocol-type {
 description
  "Base identity for protocol field type.";
}
identity tcp {
 base protocol-type;
 description
  "TCP protocol type.";
}
identity udp {
 base protocol-type;
 description
  "UDP protocol type.";
}
identity icmp {
 base protocol-type;
 description
  "ICMP protocol type.";
}
identity icmp6 {
 base protocol-type;
 description
  "ICMPv6 protocol type.";
}
identity gre {
 base protocol-type;
 description
  "GRE protocol type.";
}
identity ipip {
 base protocol-type;
 description
  "IP-in-IP protocol type.";
}
identity hop-by-hop {
 base protocol-type;
 description
  "Hop-by-Hop IPv6 header type.";
} identity routing {
 base protocol-type;
 description
  "Routing IPv6 header type.";
}
identity esp {
 base protocol-type;
 description
  "ESP header type.";
}
identity ah {
 base protocol-type;
 description
  "AH header type.";
}

leaf cloud-identifier {
 type string;
 description
  "Identification of cloud service.
  Local administration meaning.";
}
choice list-flavor {
 case permit-any {
  leaf permit-any {
   type empty;
   description
    "Allows all sites.";
  }
 }
 case deny-any-except {
  leaf-list permit-site {
   type leafref {
    path "/l3vpn-svc/sites/site/site-id";
   }
   description
    "Site ID to be authorized.";
  }
 } case permit-any-except {
  leaf-list deny-site {
   type leafref {
    path "/l3vpn-svc/sites/site/site-id";
   }
   description
    "Site ID to be denied.";
  }
 }
 description
  "Choice for cloud access policy.";
}
container authorized-sites {
 list authorized-site {
  key site-id;

  leaf site-id {
   type leafref {
    path "/l3vpn-svc/sites/site/site-id";
   }
   description
    "Site ID.";
  }
  description
   "List of authorized sites.";
 }
 description
  "Configuration of authorized sites.";
}
container denied-sites {
 list denied-site {
  key site-id;

    leaf site-id {
     type leafref {
      path "/l3vpn-svc/sites/site/site-id";
     }
     description
      "Site ID.";
    }
    description
     "List of denied sites.";
   }
   description
    "Configuration of denied sites.";
  } container address-translation {
   container nat44 {
    leaf enabled {
     type boolean;
     default false;
     description
      "Controls whether or not address translation is required.";
    }
    leaf nat44-customer-address {
     type inet:ipv4-address;
     must "../enabled = 'true'" {
      description
       "Applicable only if address translation is enabled.";
     }
     description
      "Address to be used for translation.
      This is to be used if the customer is
      providing the address.";
    }
    description
     "IPv4-to-IPv4 translation.";
   }
   description
    "Container for NAT.";
  }
  description
   "Cloud access configuration.";
 }
  description
   "Container for cloud access configurations.";
 }
 description
  "Grouping for VPN cloud definition.";
} grouping multicast-rp-group-cfg {
 choice group-format {
  case startend {
   leaf group-start {
    type inet:ip-address;
    description
     "First group address.";
   }
   leaf group-end {
    type inet:ip-address;
    description
     "Last group address.";
   }
  }
  case singleaddress {
   leaf group-address {
    type inet:ip-address;
    description
     "Group address.";
   }
  }
  description
   "Choice for group format.";
 }
 description
  "Definition of groups for RP-to-group mapping.";
}

grouping vpn-service-multicast {
 container multicast {
  if-feature multicast;
  leaf enabled {
   type boolean;
   default false;
   description
    "Enables multicast.";
  }
  container customer-tree-flavors {
   leaf-list tree-flavor {
     type identityref {
      base multicast-tree-type;
     }
     description
      "Type of tree to be used.";
   }
   description
    "Type of trees used by customer.";
  } container rp {
   container rp-group-mappings {
    list rp-group-mapping {
     key id;

leaf id {
 type uint16;
 description
  "Unique identifier for the mapping.";
}
container provider-managed {
 leaf enabled {
  type boolean;
  default false;
  description
   "Set to true if the RP must be a provider-managed node.
   Set to false if it is a customer-managed node.";
 }
 leaf rp-redundancy {
  when "../enabled = 'true'" {
   description
    "Relevant when the RP is provider-managed.";
  }
  type boolean;
  default false;
  description
   "If true, a redundancy mechanism for the RP is required.";
 }
 leaf optimal-traffic-delivery {
  when "../enabled = 'true'" {
   description
    "Relevant when the RP is provider-managed.";
  }
  type boolean;
  default false;
  description
   "If true, the SP must ensure that
   traffic uses an optimal path.";
 }
 description
  "Parameters for a provider-managed RP.";
} leaf rp-address {
 when "../provider-managed/enabled = 'false'" {
  description
   "Relevant when the RP is provider-managed.";
 }
 type inet:ip-address;
 description
  "Defines the address of the RP.
  Used if the RP is customer-managed.";
}

 leaf id {
  type uint16;
  description
   "Identifier for the group.";
 }
 uses multicast-rp-group-cfg;
 description
  "List of groups.";
}

  description
   "List of RP-to-group mappings.";
 }
 description
  "RP-to-group mappings.";
}
container rp-discovery {
 leaf rp-discovery-type {
  type identityref {
   base multicast-rp-discovery-type;
  }
  default static-rp;
  description
   "Type of RP discovery used.";
 } container bsr-candidates {
  when "../rp-discovery-type = 'bsr-rp'" {
   description
    "Only applicable if discovery type is BSR-RP.";
  }
  leaf-list bsr-candidate-address {
   type inet:ip-address;
   description
    "Address of BSR candidate.";
  }
  description
   "Customer BSR candidate's address.";
 }
 description
  "RP discovery parameters.";
}

   description
    "RP parameters.";
  }
  description
   "Multicast global parameters for the VPN service.";
 }
 description
  "Grouping for multicast VPN definition.";
}

grouping vpn-service-mpls {
 leaf carrierscarrier {
  if-feature carrierscarrier;
  type boolean;
  default false;
  description
   "The VPN is using CsC, and so MPLS is required.";
 }
 description
  "Grouping for MPLS CsC definition.";
} grouping customer-location-info {
 container locations {
  list location {
   key location-id;

   leaf location-id {
    type svc-id;
    description
     "Identifier for a particular location.";
   }
   leaf address {
    type string;
    description
     "Address (number and street) of the site.";
   }
   leaf postal-code {
    type string;
    description
     "Postal code of the site.";
   }
   leaf state {
    type string;
    description
     "State of the site.  This leaf can also be used to describe
     a region for a country that does not have states.";
   }
   leaf city {
    type string;
    description
     "City of the site.";
   }
   leaf country-code {
    type string {
     pattern '[A-Z]{2}';
    }
    description
     "Country of the site.
     Expressed as ISO ALPHA-2 code.";
   }
   description
    "Location of the site.";
   }
   description
    "List of locations for the site.";
 }
 description
  "This grouping defines customer location parameters.";
} grouping site-group {
 container groups {
  list group {
   key group-id;

   leaf group-id {
    type string;
    description
     "Group-id the site belongs to.";
   }
   description
    "List of group-ids.";
  }
  description
   "Groups the site or site-network-access belongs to.";
 }
 description
  "Grouping definition to assign
  group-ids to site or site-network-access.";
}
grouping site-diversity {
 container site-diversity {
   if-feature site-diversity;

   description
    "Diversity constraint type.
    All site-network-accesses will inherit the group values
    defined here.";
  }
 description
  "This grouping defines site diversity parameters.";
}
grouping access-diversity {
 container access-diversity {
   if-feature site-diversity;

leaf constraint-type {
 type identityref {
  base placement-diversity;
 }
 description
  "Diversity constraint type.";
}
container target {
 choice target-flavor {
  case id {
   list group {
    key group-id;

      leaf group-id {
       type string;
       description
        "The constraint will be applied against
        this particular group-id.";
      }
      description
       "List of groups.";
     }
    }
    case all-accesses {
     leaf all-other-accesses {
      type empty;
      description
       "The constraint will be applied against
       all other site network accesses of this site.";
     }
    }
    case all-groups {
     leaf all-other-groups {
      type empty;
      description
       "The constraint will be applied against
       all other groups managed by the customer.";
     }
    }
    description
     "Choice for the group definition.";
   } description
    "The constraint will be applied against
    this list of groups.";
  }
  description
   "List of constraints.";
 }
 description
  "Placement constraints for this site network access.";
}

   description
    "Diversity parameters.";
  }
 description
  "This grouping defines access diversity parameters.";
}

grouping operational-requirements {
   leaf requested-site-start {
     type yang:date-and-time;
     description
      "Optional leaf indicating requested date and time when the
      service at a particular site is expected to start.";
    }

    leaf requested-site-stop {
     type yang:date-and-time;
     description
      "Optional leaf indicating requested date and time when the
      service at a particular site is expected to stop.";
    }
 description
  "This grouping defines some operational parameters.";
} grouping operational-requirements-ops {
    leaf actual-site-start {
     type yang:date-and-time;
     config false;
     description
      "Optional leaf indicating actual date and time when the
      service at a particular site actually started.";
    }
    leaf actual-site-stop {
     type yang:date-and-time;
     config false;
     description
      "Optional leaf indicating actual date and time when the
      service at a particular site actually stopped.";
    }
 description
  "This grouping defines some operational parameters.";
}

grouping flow-definition {
 container match-flow {
  leaf dscp {
   type inet:dscp;
   description
    "DSCP value.";
  }
  leaf dot1p {
   type uint8 {
    range "0..7";
   }
   description
    "802.1p matching.";
  }
  leaf ipv4-src-prefix {
   type inet:ipv4-prefix;
   description
    "Match on IPv4 src address.";
  }
  leaf ipv6-src-prefix {
   type inet:ipv6-prefix;
   description
    "Match on IPv6 src address.";
  }
  leaf ipv4-dst-prefix {
   type inet:ipv4-prefix;
   description
    "Match on IPv4 dst address.";
  } leaf ipv6-dst-prefix {
   type inet:ipv6-prefix;
   description
    "Match on IPv6 dst address.";
  }
  leaf l4-src-port {
   type inet:port-number;
   description
    "Match on Layer 4 src port.";
  }
  leaf-list target-sites {
   type svc-id;
   description
    "Identify a site as traffic destination.";
  }
  container l4-src-port-range {
   leaf lower-port {
    type inet:port-number;
    description
     "Lower boundary for port.";
   }
   leaf upper-port {
    type inet:port-number;
    must ". >= ../lower-port" {
     description
      "Upper boundary must be higher than lower boundary.";
    }
    description
     "Upper boundary for port.";
   }
   description
    "Match on Layer 4 src port range.";
  }
  leaf l4-dst-port {
   type inet:port-number;
   description
    "Match on Layer 4 dst port.";
  }
  container l4-dst-port-range {
   leaf lower-port {
    type inet:port-number;
    description
     "Lower boundary for port.";
   } leaf upper-port {
    type inet:port-number;
    must ". >= ../lower-port" {
     description
      "Upper boundary must be higher than lower boundary.";
    }
    description
     "Upper boundary for port.";
   }
   description
    "Match on Layer 4 dst port range.";
  }
  leaf protocol-field {
   type union {
    type uint8;
    type identityref {
     base protocol-type;
    }
   }
   description
    "Match on IPv4 protocol or IPv6 Next Header field.";
  }

  description
   "Describes flow-matching criteria.";
 }
 description
  "Flow definition based on criteria.";
}
grouping site-service-basic {
 leaf svc-input-bandwidth {
     type uint32;
     units bps;
     description
      "From the PE's perspective, the service input
      bandwidth of the connection.";
 }
 leaf svc-output-bandwidth {
    type uint32;
    units bps;
    description
     "From the PE's perspective, the service output
     bandwidth of the connection.";
 } leaf svc-mtu {
  type uint16;
  units bytes;
  description
   "MTU at service level.  If the service is IP,
   it refers to the IP MTU.";
 }
 description
  "Defines basic service parameters for a site.";
}
grouping site-protection {
 container traffic-protection {
  if-feature fast-reroute;
  leaf enabled {
   type boolean;
   default false;
   description
    "Enables traffic protection of access link.";
  }
  description
   "Fast Reroute service parameters for the site.";
 }
 description
  "Defines protection service parameters for a site.";
}
grouping site-service-mpls {
 container carrierscarrier {
  if-feature carrierscarrier;
  leaf signalling-type {
   type enumeration {
    enum "ldp" {
     description
      "Use LDP as the signalling protocol
      between the PE and the CE.";
    }
    enum "bgp" {
     description
      "Use BGP (as per RFC 3107) as the signalling protocol
      between the PE and the CE.
      In this case, BGP must also be configured as
      the routing protocol.";
    }
   }
   description
    "MPLS signalling type.";
  } description
   "This container is used when the customer provides
   MPLS-based services.  This is used in the case of CsC.";
 }
 description
  "Defines MPLS service parameters for a site.";
}
grouping site-service-qos-profile {
 container qos {
  if-feature qos;
  container qos-classification-policy {
   list rule {
    key id;
    ordered-by user;

leaf id {
 type uint16;
 description
  "ID of the rule.";
}

choice match-type {
 case match-flow {
  uses flow-definition;
 }
 case match-application {
  leaf match-application {
   type identityref {
    base customer-application;
   }
   description
    "Defines the application to match.";
  }
 }
 description
  "Choice for classification.";
}

leaf target-class-id {
 type string;
 description
  "Identification of the class of service.
  This identifier is internal to the administration.";
}

  description
   "List of marking rules.";
 } description
  "Configuration of the traffic classification policy.";
}
container qos-profile {

choice qos-profile {
 description
  "Choice for QoS profile.
  Can be standard profile or custom.";
 case standard {
  leaf profile {
   type string;
   description
    "QoS profile to be used.";
  }
 }
 case custom {
  container classes {
   if-feature qos-custom;
   list class {
    key class-id;

  leaf class-id {
   type string;
   description
    "Identification of the class of service.
    This identifier is internal to the administration.";
  }
  leaf rate-limit {
   type uint8;
   units percent;
   description
    "To be used if the class must be rate-limited.
    Expressed as percentage of the service bandwidth.";
  }
  container latency {
   choice flavor {
    case lowest {
     leaf use-lowest-latency {
      type empty;
      description
       "The traffic class should use the path with the
       lowest latency.";
     }
    } case boundary {
     leaf latency-boundary {
      type uint16;
      units msec;
      description
       "The traffic class should use a path with a
       defined maximum latency.";
     }
    }
    description
     "Latency constraint on the traffic class.";
   }
   description
    "Latency constraint on the traffic class.";
  }
  container jitter {
   choice flavor {
    case lowest {
     leaf use-lowest-jitter {
      type empty;
      description
       "The traffic class should use the path with the
       lowest jitter.";
     }
