module ietf-routing-types {
  namespace "urn:ietf:params:xml:ns:yang:ietf-routing-types";
  prefix rt-types;

import ietf-yang-types {
  prefix yang;
}
import ietf-inet-types {
  prefix inet;
}

 revision 2017-12-04 {
   description "Initial revision.";
   reference
     "RFC 8294: Common YANG Data Types for the Routing Area.
      Section 3.";
}

identity mpls-label-special-purpose-value {
  description
    "Base identity for deriving identities describing
     special-purpose Multiprotocol Label Switching (MPLS) label
     values.";
  reference
    "RFC 7274: Allocating and Retiring Special-Purpose MPLS
     Labels.";
}

identity ipv4-explicit-null-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the IPv4 Explicit NULL Label.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.  Section 2.1.";
}

identity router-alert-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the Router Alert Label.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.  Section 2.1.";
}

identity ipv6-explicit-null-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the IPv6 Explicit NULL Label.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.  Section 2.1.";
} identity implicit-null-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the Implicit NULL Label.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.  Section 2.1.";
}

identity entropy-label-indicator {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the Entropy Label Indicator.";
  reference
    "RFC 6790: The Use of Entropy Labels in MPLS Forwarding.
     Sections 3 and 10.1.";
}

identity gal-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the Generic Associated Channel
     (G-ACh) Label (GAL).";
  reference
    "RFC 5586: MPLS Generic Associated Channel.
     Sections 4 and 10.";
}

identity oam-alert-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the OAM Alert Label.";
  reference
    "RFC 3429: Assignment of the 'OAM Alert Label' for
     Multiprotocol Label Switching Architecture (MPLS)
     Operation and Maintenance (OAM) Functions.
     Sections 3 and 6.";
}

identity extension-label {
  base mpls-label-special-purpose-value;
  description
    "This identity represents the Extension Label.";
  reference
    "RFC 7274: Allocating and Retiring Special-Purpose MPLS
     Labels.  Sections 3.1 and 5.";
}

typedef router-id {
  type yang:dotted-quad;
  description
    "A 32-bit number in the dotted-quad format assigned to each
     router.  This number uniquely identifies the router within
     an Autonomous System.";
}

     Two valid examples are 2001:db8::1:6544 and
     2001:db8::5eb1:791:6b37:17958.";
  reference
    "RFC 5701: IPv6 Address Specific BGP Extended Community
     Attribute.";
}

typedef route-target-type {
  type enumeration {
    enum import {
      value 0;
      description
        "The Route Target applies to route import.";
    }
    enum export {
      value 1;
      description
        "The Route Target applies to route export.";
    } enum both {
      value 2;
      description
        "The Route Target applies to both route import and
         route export.";
    }
  }
  description
    "Indicates the role a Route Target takes in route filtering.";
  reference
    "RFC 4364: BGP/MPLS IP Virtual Private Networks (VPNs).";
}

     Some valid examples are 0:100:100, 1:1.1.1.1:100,
     2:1234567890:203, and 6:26:00:08:92:78:00.";
  reference
    "RFC 4360: BGP Extended Communities Attribute.
     RFC 4364: BGP/MPLS IP Virtual Private Networks (VPNs).
     RFC 5668: 4-Octet AS Specific BGP Extended Community.
     RFC 7432: BGP MPLS-Based Ethernet VPN.";
}

     Two valid examples are 2001:db8::1:6544 and
     2001:db8::5eb1:791:6b37:17958.";
  reference
    "RFC 5701: IPv6 Address Specific BGP Extended Community
     Attribute.";
}

typedef ipv4-multicast-group-address {
  type inet:ipv4-address {
    pattern '(2((2[4-9])|(3[0-9]))\.).*';
  }
  description
    "This type represents an IPv4 multicast group address,
     which is in the range of 224.0.0.0 to 239.255.255.255.";
  reference
    "RFC 1112: Host Extensions for IP Multicasting.";
}

typedef ipv6-multicast-group-address {
  type inet:ipv6-address {
    pattern '(([fF]{2}[0-9a-fA-F]{2}):).*';
  }
  description
    "This type represents an IPv6 multicast group address,
     which is in the range of ff00::/8.";
  reference
    "RFC 4291: IP Version 6 Addressing Architecture.  Section 2.7.
     RFC 7346: IPv6 Multicast Address Scopes.";
}

typedef ip-multicast-group-address {
  type union {
    type ipv4-multicast-group-address;
    type ipv6-multicast-group-address;
  }
  description
    "This type represents a version-neutral IP multicast group
     address.  The format of the textual representation implies
     the IP version.";
} typedef ipv4-multicast-source-address {
  type union {
    type enumeration {
      enum * {
        description
          "Any source address.";
      }
    }
    type inet:ipv4-address;
  }
  description
    "Multicast source IPv4 address type.";
}

typedef ipv6-multicast-source-address {
  type union {
    type enumeration {
      enum * {
        description
          "Any source address.";
      }
    }
    type inet:ipv6-address;
  }
  description
    "Multicast source IPv6 address type.";
}

typedef link-access-type {
  type enumeration {
    enum broadcast {
      description
        "Specify broadcast multi-access network.";
    }
    enum non-broadcast-multiaccess {
      description
        "Specify Non-Broadcast Multi-Access (NBMA) network.";
    }
    enum point-to-multipoint {
      description
        "Specify point-to-multipoint network.";
    }
    enum point-to-point {
      description
        "Specify point-to-point network.";
    }
  }
  description
    "Link access type.";
}

typedef timer-multiplier {
  type uint8;
  description
    "The number of timer value intervals that should be
     interpreted as a failure.";
} typedef timer-value-seconds16 {
  type union {
    type uint16 {
      range "1..65535";
    }
    type enumeration {
      enum infinity {
        description
          "The timer is set to infinity.";
      }
      enum not-set {
        description
          "The timer is not set.";
      }
    }
  }
  units "seconds";
  description
    "Timer value type, in seconds (16-bit range).";
}

typedef timer-value-seconds32 {
  type union {
    type uint32 {
      range "1..4294967295";
    }
    type enumeration {
      enum infinity {
        description
          "The timer is set to infinity.";
      }
      enum not-set {
        description
          "The timer is not set.";
      }
    }
  }
  units "seconds";
  description
    "Timer value type, in seconds (32-bit range).";
} typedef timer-value-milliseconds {
  type union {
    type uint32 {
      range "1..4294967295";
    }
    type enumeration {
      enum infinity {
        description
          "The timer is set to infinity.";
      }
      enum not-set {
        description
          "The timer is not set.";
      }
    }
  }
  units "milliseconds";
  description
    "Timer value type, in milliseconds.";
}

typedef percentage {
  type uint8 {
    range "0..100";
  }
  description
    "Integer indicating a percentage value.";
}

typedef timeticks64 {
  type uint64;
  description
    "This type is based on the timeticks type defined in
     RFC 6991, but with 64-bit width.  It represents the time,
     modulo 2^64, in hundredths of a second between two epochs.";
  reference
    "RFC 6991: Common YANG Data Types.";
}

typedef uint24 {
  type uint32 {
    range "0..16777215";
  }
  description
    "24-bit unsigned integer.";
}

typedef generalized-label {
  type binary;
  description
    "Generalized Label.  Nodes sending and receiving the
     Generalized Label are aware of the link-specific
     label context and type.";
  reference
    "RFC 3471: Generalized Multi-Protocol Label Switching (GMPLS)
     Signaling Functional Description.  Section 3.2.";
}

typedef mpls-label-special-purpose {
  type identityref {
    base mpls-label-special-purpose-value;
  }
  description
    "This type represents the special-purpose MPLS label values.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.
     RFC 7274: Allocating and Retiring Special-Purpose MPLS
     Labels.";
}

typedef mpls-label-general-use {
  type uint32 {
    range "16..1048575";
  }
  description
    "The 20-bit label value in an MPLS label stack as specified
     in RFC 3032.  This label value does not include the
     encodings of Traffic Class and TTL (Time to Live).
     The label range specified by this type is for general use,
     with special-purpose MPLS label values excluded.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.";
} typedef mpls-label {
  type union {
    type mpls-label-special-purpose;
    type mpls-label-general-use;
  }
  description
    "The 20-bit label value in an MPLS label stack as specified
     in RFC 3032.  This label value does not include the
     encodings of Traffic Class and TTL.";
  reference
    "RFC 3032: MPLS Label Stack Encoding.";
}

  grouping mpls-label-stack {
    description
      "This grouping specifies an MPLS label stack.  The label
       stack is encoded as a list of label stack entries.  The
       list key is an identifier that indicates the relative
       ordering of each entry, with the lowest-value identifier
       corresponding to the top of the label stack.";
    container mpls-label-stack {
      description
        "Container for a list of MPLS label stack entries.";
      list entry {
        key "id";
        description
          "List of MPLS label stack entries.";
        leaf id {
          type uint8;
          description
            "Identifies the entry in a sequence of MPLS label
             stack entries.  An entry with a smaller identifier
             value precedes an entry with a larger identifier
             value in the label stack.  The value of this ID has
             no semantic meaning other than relative ordering
             and referencing the entry.";
        }
        leaf label {
          type rt-types:mpls-label;
          description
            "Label value.";
        } leaf ttl {
          type uint8;
          description
            "Time to Live (TTL).";
          reference
            "RFC 3032: MPLS Label Stack Encoding.";
        }
        leaf traffic-class {
          type uint8 {
            range "0..7";
          }
          description
            "Traffic Class (TC).";
          reference
            "RFC 5462: Multiprotocol Label Switching (MPLS) Label
             Stack Entry: 'EXP' Field Renamed to 'Traffic Class'
             Field.";
        }
      }
    }
  } grouping vpn-route-targets {
    description
      "A grouping that specifies Route Target import-export rules
       used in BGP-enabled VPNs.";
    reference
      "RFC 4364: BGP/MPLS IP Virtual Private Networks (VPNs).
       RFC 4664: Framework for Layer 2 Virtual Private Networks
       (L2VPNs).";
    list vpn-target {
      key "route-target";
      description
        "List of Route Targets.";
      leaf route-target {
        type rt-types:route-target;
        description
          "Route Target value.";
      }
      leaf route-target-type {
        type rt-types:route-target-type;
        mandatory true;
        description
          "Import/export type of the Route Target.";
      }
    }
  }
}

